mapper.select:
  	SELECT * FROM
	  	(SELECT ROWNUM R, Q.* FROM
			(SELECT * FROM QNA



	  		ORDER BY REF DESC, STEP ASC
	  		) Q
	  	)
	  	WHERE R BETWEEN #{startRow} AND #{lastRow}

mapper.update:
  		UPDATE QNA SET
  		BOARD_TITLE = #{board_title}, BOARD_CONTENTS = #{board_contents}, UPDATE_DATE = SYSDATE
			WHERE BOARD_NUM = #{board_num}
